// Получаем данные от backend
const userData = $input.item.json;
const branch = userData.branch || 'power';
const gender = userData.gender || 'male';
const age = userData.age || 25;
const level = userData.level || 1;
const selfieUrl = userData.selfie_url;

// Определяем стиль одежды в зависимости от ветки
const clothingStyles = {
  power: {
    male: 'athletic sportswear, gym hoodie and joggers, sneakers, confident pose',
    female: 'athletic sportswear, sports bra and leggings, sneakers, strong pose'
  },
  stability: {
    male: 'casual business attire, button-up shirt and chinos, loafers, calm pose',
    female: 'business casual, blouse and trousers, ballet flats, balanced pose'
  },
  longevity: {
    male: 'comfortable casual wear, t-shirt and jeans, running shoes, relaxed pose',
    female: 'comfortable casual wear, flowy top and yoga pants, sneakers, peaceful pose'
  }
};

// Получаем одежду для конкретной ветки и пола
const clothing = clothingStyles[branch][gender];

// Уровень влияет на детализацию
const qualityLevel = level <= 10 ? 'good quality' : level <= 25 ? 'high quality, detailed' : 'ultra high quality, highly detailed, polished';

// Создаем промпт для Nano Banana
const prompt = `Transform this person into a 3D cartoon character while maintaining their facial features, hairstyle, and overall appearance. Style: vibrant, colorful, animated movie quality, pixar-like rendering. The character should be wearing ${clothing}. Age appearance: ${age} years old. Quality: ${qualityLevel}, smooth textures, professional lighting. Background: clean gradient (light to dark), no distractions. The character should look heroic but realistic, not a superhero costume.`;

// Возвращаем все данные + промпт
return {
  user_id: userData.user_id,
  tg_id: userData.tg_id,
  selfie_url: selfieUrl,
  branch: branch,
  level: level,
  gender: gender,
  age: age,
  prompt: prompt
};

// Получаем результат от Nano Banana
const response = $input.item.json;
const userData = $input.item.json;

// Извлекаем URL сгенерированного изображения
let avatarUrl = null;

if (response.generatedImages && response.generatedImages.length > 0) {
  // Gemini возвращает base64, нужно либо сохранить, либо получить URL
  const imageData = response.generatedImages[0];

  // Если это base64, конвертируем в URL (через хранилище)
  if (imageData.imageData) {
    avatarUrl = `data:image/png;base64,${imageData.imageData}`;
  } else if (imageData.uri) {
    avatarUrl = imageData.uri;
  }
}

// Если URL не получен, используем заглушку
if (!avatarUrl) {
  avatarUrl = 'https://placehold.co/400x400/FF6B35/FFF?text=Hero';
}

return {
  user_id: userData.user_id,
  tg_id: userData.tg_id,
  avatar_url: avatarUrl,
  level: userData.level,
  branch: userData.branch
};
