// Получаем данные от backend
const userData = $input.item.json;
const branch = userData.branch || 'power';
const gender = userData.gender || 'male';
const age = userData.age || 25;
const level = userData.level || 1;
const selfieUrl = userData.selfie_url;

// Определяем стиль одежды в зависимости от ветки
const clothingStyles = {
  power: {
    male: 'wearing athletic sportswear: black hoodie, grey joggers, white sneakers',
    female: 'wearing athletic sportswear: sports top, black leggings, white sneakers'
  },
  stability: {
    male: 'wearing business casual: blue button-up shirt, beige chinos, brown loafers',
    female: 'wearing business casual: white blouse, black trousers, elegant flats'
  },
  longevity: {
    male: 'wearing comfortable casual: plain t-shirt, blue jeans, comfortable sneakers',
    female: 'wearing comfortable casual: casual top, yoga pants, comfortable sneakers'
  }
};

// Получаем одежду для конкретной ветки и пола
const clothing = clothingStyles[branch][gender];

// Промпт для fal.ai (image-to-image)
const prompt = `3D cartoon character, animated movie style, pixar-like quality, ${clothing}, standing confidently, clean gradient background, vibrant colors, smooth textures, professional lighting, high quality render, maintaining facial features and hairstyle from reference image`;

// Negative prompt (что НЕ должно быть)
const negativePrompt = 'superhero costume, cape, mask, armor, weapons, low quality, blurry, distorted, ugly, bad anatomy, extra limbs';

// Параметры в зависимости от уровня
const strength = level <= 10 ? 0.75 : level <= 25 ? 0.70 : 0.65; // Чем выше уровень, тем больше детализация

// Возвращаем все данные
return {
  user_id: userData.user_id,
  tg_id: userData.tg_id,
  selfie_url: selfieUrl,
  branch: branch,
  level: level,
  gender: gender,
  age: age,
  prompt: prompt,
  negative_prompt: negativePrompt,
  strength: strength
};

// Получаем результат от fal.ai
const response = $input.item.json;
const prevData = $('Generate Prompt').item.json;

// Извлекаем URL сгенерированного изображения
let avatarUrl = null;

if (response.images && response.images.length > 0) {
  avatarUrl = response.images[0].url;
} else if (response.image && response.image.url) {
  avatarUrl = response.image.url;
} else if (response.url) {
  avatarUrl = response.url;
}

// Если URL не получен, используем заглушку
if (!avatarUrl) {
  console.error('Avatar URL not found in response:', response);
  avatarUrl = 'https://placehold.co/512x512/FF6B35/FFF?text=Hero+Level+' + prevData.level;
}

return {
  user_id: prevData.user_id,
  tg_id: prevData.tg_id,
  avatar_url: avatarUrl,
  level: prevData.level,
  branch: prevData.branch,
  generation_success: !!avatarUrl
};
